import React from 'react'

export const Scroll = ({children})=>{
    return (
        <div style={{overflow: "scroll", border:"1px solid black", height:"30em",scrollbarWidth: "thin"}}>
            {children}
            {`
          ::-webkit-scrollbar {
            width: 0;  /* Set width to 0 to hide scrollbar in webkit browsers */
          }
        `}
        </div>
    )
}
